    //! @brief returns class name
    //! @return the class name as const ref std::string
    const std::string &ElementTypes::GetClassIdentifier() const
    {
      return GetStaticClassName( *this);
    }

    //! @brief create a simple map containing all element type strings to the related element type
    std::map< std::string, ElementType> GetStringToElementTypeMap()
    {
      std::map< std::string, ElementType> element_types_map;
      for
      (
        ElementTypes::const_iterator itr( GetElementTypes().Begin()), itr_end( GetElementTypes().End());
        itr != itr_end;
        ++itr
      )
      {
        element_types_map[ ( *itr)->GetChemicalSymbol()] = *itr;
      }

      // aliases
      element_types_map[ "X"] = GetElementTypes().e_Undefined;
      return element_types_map;
    }

    //! obtain ElementTypes from symbol
    //! @param SYMBOL the atomic symbol; may also contain isotopic information
    //! @param QUIET reduce the volume of message output from CRT to VRB
    ElementType ElementTypes::ElementTypeLookup( const std::string &SYMBOL, const bool QUIET) const
    {
      // get the constant string to element type map
      static std::map< std::string, ElementType> s_element_types_map( GetStringToElementTypeMap());

      if( SYMBOL.empty())
      {
        if( QUIET)
        {
          BCL_MessageVrb( "Element without symbol given!");
        }
        else
        {
          BCL_MessageCrt( "Element without symbol given!");
        }
        return GetElementTypes().e_Undefined;
      }

      // look for the string in the map
      std::map< std::string, ElementType>::const_iterator itr_map( s_element_types_map.find( SYMBOL));

      // if the string was found, return it
      if( itr_map != s_element_types_map.end())
      {
        return itr_map->second;
      }

      // if the string is too small for other possible transformations, return undefined
      if( SYMBOL.size() == size_t( 1))
      {
        if( SYMBOL[ 0] == 'D' || SYMBOL[ 0] == 'T')
        {
          // deuterium or tritium
          BCL_MessageStd
          (
            "Warning: Interpreting isotope " + SYMBOL +
            " as elemental Hydrogen, properties such as atom/molecular masses and gyromagnetic ratio will be incorrect"
          );
        }
        return GetElementTypes().e_Hydrogen;
      }
      else if( SYMBOL.size() == 2 && SYMBOL[ 1] == 'H' && isdigit( SYMBOL[ 0]))
      {
        BCL_MessageStd
        (
          "Warning: Interpreting isotope " + SYMBOL +
          " as elemental Hydrogen, properties such as atom/molecular masses and gyromagnetic ratio will be incorrect"
        );
        return GetElementTypes().e_Hydrogen;
      }

      // usually the reasons for not finding the element type are that the user has put the isotope at the beginning
      // or end of the element name.  These are usually unambiguous, but the loss of isotopic information should be
      // noted to the user

      // try finding the first capital letter
      size_t first_letter( 0);
      const size_t name_size( SYMBOL.size());
      while( first_letter < name_size && !isupper( SYMBOL[ first_letter]))
      {
        ++first_letter;
      }

      // if none was found, try looking for any letter instead
      if( first_letter == name_size)
      {
        first_letter = 0;
        while( first_letter < name_size && !isalpha( SYMBOL[ first_letter]))
        {
          ++first_letter;
        }

        if( first_letter == name_size)
        {
          if( QUIET)
          {
            BCL_MessageVrb( "Element with invalid symbol found: " + SYMBOL);
          }
          else
          {
            BCL_MessageCrt( "Element with invalid symbol found: " + SYMBOL);
          }
          return GetElementTypes().e_Undefined;
        }
      }

      // check whether the next letter, if any, is lower case, if so, include it
      size_t element_name_size( 1);
      if( first_letter + 1 < name_size && islower( SYMBOL[ first_letter + 1]))
      {
        element_name_size = 2;
      }
      else if( first_letter + 2 == name_size && isupper( SYMBOL[ first_letter + 1]))
      {
        std::string nominal_name( SYMBOL.substr( first_letter, 2));
        if( !biol::AtomType( nominal_name).IsDefined())
        {
          nominal_name[ 1] = std::tolower( nominal_name[ 1]);
          itr_map = s_element_types_map.find( nominal_name);
          if( itr_map != s_element_types_map.end())
          {
            // innocent capitalization
            BCL_MessageStd
            (
              "Improper capitalization of 2nd letter of element type. Interpreting "
              + SYMBOL + " as " + itr_map->second->GetChemicalName()
            );
            return itr_map->second;
          }
        }
        else
        {
          itr_map = s_element_types_map.find( SYMBOL.substr( first_letter, element_name_size));
          if( itr_map != s_element_types_map.end())
          {
            BCL_ExitWithoutCallstack
            (
              SYMBOL
              + " cannot be resolved. It could refer to either "
              + itr_map->second->GetChemicalName()
              + " or could be a PDB atom type name (imported via open babel or corina)"
                ". Please correct the capitalization or remove the PDB atom name designation",
              -1
            );
          }
        }
      }
      // look for the string in the map

      if( itr_map != s_element_types_map.end())
      {
        if( QUIET)
        {
          BCL_MessageVrb
          (
            "Warning: Interpreting " + SYMBOL + " as elemental " + itr_map->second->GetChemicalName()
          );
        }
        else
        {
          BCL_MessageCrt
          (
            "Warning: Interpreting " + SYMBOL + " as elemental " + itr_map->second->GetChemicalName()
          );
        }
        return itr_map->second;
      }

      // end
      return GetElementTypes().e_Undefined;
    }

    const ElementTypes &GetElementTypes()
    {
      return ElementTypes::GetEnums();
    }

